<?phpfunction get_c_language(){    $data = [        ['value'=>'English','name'=>'English'],        ['value'=>'Spanish','name'=>'Spanish']    ];    return $data;}function get_c_how_did_the_customer_reach_us(){    $data = [        ['value'=>'Referral','name'=>'Referral'],        ['value'=>'Dealer Group','name'=>'Dealer Group'],        ['value'=>'Self Gen','name'=>'Self Gen'],    ];    return $data;}if (!function_exists('get_fund_c_all_clients')) {    function get_fund_c_all_clients()    {        $CI =& get_instance();        $data = $CI->db->select('userid,company')->from('tblclients')->get()->result_array();        foreach ($data as $k => $v) {            if (empty($v['company'])) {                $fullname = $CI->db->select('firstname,lastname')                    ->from('tblcontacts')                    ->where('userid', $v['userid'])                    ->where('is_primary', 1)                    ->get()->row();                $firstname = isset($fullname->firstname) ? $fullname->firstname : '';                $lastname = isset($fullname->lastname) ? $fullname->lastname : '';                $data[$k]['company'] = $firstname . ' ' . $lastname;            }        }        return $data;    }}/*function related_handle_upload_file_arr($uploadDir,$file_name,$tmpFile,$id,$related){    if (!is_dir($uploadDir. "/")) {        mkdir($uploadDir . "/", 0777, true);    }    $extension = pathinfo($file_name, PATHINFO_EXTENSION);    $random_name = date('YmdHis').related_generate_random_string(10).'.'.$extension;    $filename = $uploadDir.'/'.$random_name;    $CI =& get_instance();    if (move_uploaded_file($tmpFile, $filename)){        $CI->db->insert('tblfiles',['rel_type'=>$related,'rel_id'=>$id,'file_name'=>$random_name]);    }    return $random_name;}*/if (!function_exists('related_handle_upload_file')) {    function related_handle_upload_file($uploadDir, $file_name, $tmpFile)    {        if (!is_dir($uploadDir . "/")) {            mkdir($uploadDir . "/", 0777, true);        }        $extension = pathinfo($file_name, PATHINFO_EXTENSION);        $random_name = date('YmdHis') . related_generate_random_string(10) . '.' . $extension;        $filename = $uploadDir . '/' . $random_name;        move_uploaded_file($tmpFile, $filename);        return $random_name;    }}if (!function_exists('related_generate_random_string')) {    function related_generate_random_string($length = 5)    {        $characters = '0123456789abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ';        $charactersLength = strlen($characters);        $randomString = '';        for ($i = 0; $i < $length; $i++) {            $randomString .= $characters[rand(0, $charactersLength - 1)];        }        return $randomString;    }}if (!function_exists('get_custom_supplier')) {    function get_custom_supplier()    {        $CI =& get_instance();        $data = $CI->db->select('userid,company')            ->from('tblclients')            ->where('is_supplier', 1)            ->where('active', 1)            ->get()->result_array();        return $data;    }}