<?phpdefined('BASEPATH') or exit('No direct script access allowed');$CI =& get_instance();if (!$CI->db->table_exists(db_prefix() . '_customer_profile')) {    $CI->db->query('CREATE TABLE `' . db_prefix() . '_customer_profile`      (        `id` int(11) NOT NULL AUTO_INCREMENT,        `first_name` varchar(255) NULL DEFAULT NULL,        `last_name` varchar(255) NULL DEFAULT NULL,        `language` varchar(255) NULL DEFAULT NULL,        `phone_number` varchar(255) NULL DEFAULT NULL,        `email` varchar(255) NULL DEFAULT NULL,        `address` varchar(255) NULL DEFAULT NULL,        `city` varchar(255) NULL DEFAULT NULL,        `zip_ode` varchar(255) NULL DEFAULT NULL,        `how_did_the_customer_reach_us` varchar(255) NULL DEFAULT NULL,        `referral_name` varchar(255) NULL DEFAULT NULL,        `referral` varchar(255) NULL DEFAULT NULL,        `co_borrower_full_name` varchar(255) NULL DEFAULT NULL,        `co_borrower_phonenumber` varchar(255) NULL DEFAULT NULL,        `co_borrower_e_mail` varchar(255) NULL DEFAULT NULL,        `active` TINYINT(1) NULL DEFAULT 1,        `is_deleted` TINYINT(1) NULL DEFAULT 0,        `created` datetime DEFAULT CURRENT_TIMESTAMP,        `modified_at` datetime DEFAULT NULL ON UPDATE current_timestamp(),        PRIMARY KEY (`id`)    ) ENGINE=InnoDB DEFAULT CHARSET=latin1;');}if (!$CI->db->field_exists('created_by', '_customer_profile')) {    $CI->db->query('ALTER TABLE `' . db_prefix() . '_customer_profile`  ADD COLUMN `created_by` INT(11) NULL DEFAULT NULL');}